
public class RoundingNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        
		
		double y=  ((Math.floor(5*100+0.5) / 100));
		
		System.out.println(y);
		System.out.println((Math.floor(5*100+0.5)));
		System.out.println(Math.floor(42/10)*10);
		System.out.println(Math.floor(422/100)*100);
		System.out.println(Math.floor(1222/1000)*1000);
	}

}
